import React, {useState} from 'react';
import { useNavigate } from "react-router-dom";

import { Tabs, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
import PageTitle from '../components/pagetitle';

import {Link} from 'react-router-dom'

Register.propTypes = {
};

function Register(props) {
    const [formData, setFormData] = useState({
        email: '',
        nickname: '',
        password: '',
        country: ''
    });

    const navigate = useNavigate();

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.id]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        try {
            const response = await fetch("localhost:8080//api/user/register", {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(formData)
            });

            console.log("WTF")
            if (!response.ok) {
                throw new Error("Network response was not ok");
            }

            const status = await response.status;
            console.log(status)

            if (status === 201) {
                navigate("/");
            }

        } catch (error) {
            console.error(error);
        }
    };


    return (
        <div>
            <PageTitle heading='Register' title='Register' />

            <section className="register">
            <div className="container">
                <div className="row">
                <div className="col-md-12">
                    <div className="block-text center">
                    <h3 className="heading">Register To Rockie</h3>
                    <p className="desc fs-20">
                        Register in advance and enjoy the event benefits
                    </p>
                    </div>
                </div>
                <div className="col-md-12">
                <Tabs>
                    <TabPanel>
                        <div className="content-inner">
                            <form onSubmit={handleSubmit}>
                                <div className="form-group">
                                <label for="email">Email</label>
                                <input
                                    type="email"
                                    className="form-control"
                                    id="email"
                                    placeholder="Please fill in the email form."
                                    onChange={handleChange}
                                    required
                                />
                                </div>
                                <div className="form-group">
                                <label
                                    >Password
                                    <span
                                    >(8 or more characters, including numbers and special
                                    characters)</span
                                    ></label
                                >
                                <input
                                    type="password"
                                    className="form-control mb-10"
                                    id="password"
                                    placeholder="Please enter a password."
                                    onChange={handleChange}
                                    required
                                />
                                <input
                                    type="password"
                                    className="form-control"
                                    id="password2"
                                    placeholder="Please re-enter your password."
                                    onChange={handleChange}
                                    required
                                />
                                </div>
                                <div className="form-group">
                                <label for="nickname"
                                    >NickName
                                    <span className="fs-14"
                                    >(Excluding special characters)</span
                                    ></label
                                >
                                <input
                                    type="text"
                                    className="form-control"
                                    id="nickname"
                                    placeholder="Enter nickname"
                                    onChange={handleChange}
                                    required
                                />
                                </div>
                                <div className="form-group">
                                <label for="country">Country </label>
                                <select className="form-control" onChange={handleChange} id="country" required>
                                    <option selected value="">Choose country</option>
                                    <option>Russia Federation</option>
                                    <option>USA</option>
                                    <option>South Korea</option>
                                </select>
                                </div>
                                <button type="submit" className="btn-action">
                                Registration
                                </button>
                                <div className="bottom">
                                <p>Already have an account?</p>
                                <Link to="/login">Login</Link>
                                </div>
                            </form>
                        </div>
                    </TabPanel>
                </Tabs> 

                </div>
                </div>
            </div>
            </section>
            
        </div>
    );
}

export default Register;
